def __clear_mods__():
    try:
        del sys.modules["pip"]
    except KeyError:
        pass

    try:
        del sys.modules["pdb"]
    except KeyError:
        pass

    try:
        del sys.modules["logging"]
        del sys.modules["logging.config"]
        del sys.modules["logging.handlers"]
    except KeyError:
        pass

    try:
        del sys.modules["pyto_ui"]
    except KeyError:
        pass

    try:
        del sys.modules["turtle"]
    except KeyError:
        pass

    try:
        _values = sys.modules["_values"]
        
        for attr in dir(_values):
            if attr not in _values._dir:
                delattr(_values, attr)
            
    except:
        pass

    try:
        if "matplotlib" in sys.modules:
            import matplotlib.pyplot as plt

            plt.close()
            plt.clf()
    except:
        pass

    keys = list(sys.modules.keys())
    for key in keys:
        try:
            mod = sys.modules[key]
            if os.access(mod.__file__, os.W_OK) and not "/Library/python38" in mod.__file__:
                del sys.modules[key]
        except AttributeError as e:
            pass
        except TypeError as e:
            pass


