#!/bin/bash

shopt -s extglob

rm -rfv !("requirements.txt"|"install.sh")
python3 -m pip install --no-deps -r requirements.txt -t .

find . -name "__pycache__" -exec rm -rf {} \;

cd ../downloadable-site-packages
./install.sh
cd ../
Extensions/Dependencies/tools/build-matplotlib.sh
Extensions/Dependencies/tools/build-pillow.sh
Extensions/Dependencies/tools/build-biopython.sh
Extensions/Dependencies/tools/build-erfa.sh
Extensions/Dependencies/tools/copy-eggs.sh
